// src/App.js
import React from 'react';
import { useAuth } from '.AuthContext.js'; // Ensure the path is correct
import AuthComponent from './components/AuthComponent'; // Import your AuthComponent


const App = () => {
    const { user } = useAuth();

    return (
        <div>
            <h1>Welcome to the Pomodoro Timer App</h1>
            {user ? (
                <div>
                    <p>Hello, {user.email}</p>
                    {/* Include other components for authenticated users */}
                </div>
            ) : (
                <AuthComponent /> // Render your AuthComponent for sign in
            )}
        </div>
    );
};

export default App;
